(()=>{function e(){document.body.className.includes("dark")?(document.body.classList.remove("dark"),localStorage.setItem("theme","light")):(document.body.classList.add("dark"),localStorage.setItem("theme","dark"))}window.addEventListener("DOMContentLoaded",t=>{const n=document.getElementById("theme-switcher");n&&n.addEventListener("click",()=>{e()}),window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",t=>{t.matches&&localStorage.getItem("theme")==="light"&&e()})})})()